INSERT INTO [dbo].[Product] ([ProductID], [NamePro], [DecriptionPro], [CateID], [Price], [ImagePro], [Quantity]) VALUES (3, N'Nike React Infinity Run Flyknit 3', N'Nike là một trong những thương hiệu thể thao nổi tiếng nhất trên thế giới được thành lập tại Mỹ. Nike là nhà cung cấp giày thể thao lớn nhất trên thế giới và cũng là một nhà sản xuất thiết bị thể thao danh tiếng. Những đôi giày thể thao của Nike rất được ưu chuộng trên toàn thế giới thông qua các con số doanh thu khổng lồ mà Nike nhận được.Khách hàng từ mọi lứu tuổi , từ mọi từng lớp đều rất yêu thích những đôi giày mà Nike mang tới cho các tín đồ yêu thích giày trên toàn thế giới. Chúng ta có thể bắt gặp rất nhiều siêu sao, người mẫu, ca sĩ mang trên chân những đôi giày vô cùng đẹp và độc đáo đến từ thương hiệu này và điều đó làm cho Nike càng thêm trendy.Ưu điểm của những đôi giày Nike mang lại vô cùng thoải mái và vô cùng đa dạng màu sắc cũng nhiêu kiểu dáng , mang đến nhiều sự lựa chọn cho mọi người.
', 3, CAST(3600000.00 AS Decimal(18, 2)), N'~/Content/images/nike_RIRFlyknit3.jpg', 100)
